INSERT INTO news(id,user_id,title,slug,description,content,image,date,address,created_at,updated_at) VALUES (6,1,'KAMPUNG WARNA WARNI TIGARIHIT MASUK 50 BESAR DESA WISATA TERBAIK SE INDONESIA','kampung-warna-warni-tigarihit-masuk-50-besar-desa-wisata-terbaik-se-indonesia',NULL,'<p>&nbsp;Desa Wisata Kampung Warna warni Tigarihit parapat berhasil menjadi salah satu dari 50 desa wisata terbaik dalam ajang Anungerah Desa Wisata Indonesia 2022 yang dilaksanakan oleh Kemenparekraf RI dan diikuti oleh 3400 desa wisata se&nbsp; Indonesia. Pada malam puncak Anugerah Desa Wisata Indonesia 2022 yang diselenggarakan di Gedung Sapta Pesona Kemenparekraf RI, Jakarta Pusat, Desa wisata Kampung Warna Warni juga berhasil meraih penghargaan Juara Harapan 2 dalam kategori CHSE&nbsp;<span style="font-size: 1rem;">&nbsp;</span><span style="font-size: 1rem;">(clean,health,safety,environment)</span><span style="font-size: 1rem;">&nbsp;yang diserahkan langsung oleh Bapak Menparekraf RI Sandiaga Salahuddin Uno dan diterima langsung oleh Pengelola Desa Wisata Kampung Warna Warni Tigarihit Mike Wijaya Sinaga didampingi oleh Kepala Dinas Pariwisata dan Kebudayaan Simalungun Bapak M. Fikri F Damanik, S.IP,M.Si.</span></p><p><span style="font-size: 1rem;">by admin</span></p>','news/1667189391-kampung-warna-warni-tigarihit-masuk-50-besar-desa-wisata-terbaik-se-indonesia-news.png','2022-10-30','Kementerian Pariwisata dan Ekonomi Kreatif/ Badan Pariwisata dan Ekonomi Kreatif Republik Indonesia, Jalan Medan Merdeka Barat, RT.2/RW.3, Gambir, Kota Jakarta Pusat, Daerah Khusus Ibukota Jakarta, Indonesia','2022-10-31','2022-10-31');
INSERT INTO news(id,user_id,title,slug,description,content,image,date,address,created_at,updated_at) VALUES (7,1,'PAGELARAN BUDAYA DAN DRAMA MUSIKAL TOPING TOPING DAN HUDA HUDA','pagelaran-budaya-dan-drama-musikal-toping-toping-dan-huda-huda',NULL,'<p><u>SIARAN PERS DINAS PARIWISATA DAN KEBUDAYAAN KABUPATEN SIMALUNGUN&nbsp;</u></p><p><b>PAGELARAN BUDAYA DAN DRAMA MUSIKAL TOPING - TOPING HUDA - HUDA SIMALUNGUN DI RTP PANTAI BEBAS PARAPAT</b>&nbsp;</p><p>07 NOVEMBER 2022 ; PEMATANG RAYA&nbsp;</p><p>DINAS PARIWISATA DAN KEBUDAYAAN KABUPATEN SIMALUNGUN AKAN MELAKSANAKAN KEGIATAN&nbsp;<span style="font-size: 1rem; font-weight: bolder;">PAGELARAN BUDAYA DAN DRAMA MUSIKAL TOPING - TOPING HUDA - HUDA SIMALUNGUN DI RTP PANTAI BEBAS PARAPAT PADA TANGGAL 12 NOVEMBER 2022, </span><span style="font-size: 1rem;">PAGELARAN INI BERTUJUAN UNTUK MELESTARIKAN BUDAYA TOPING TOPING DAN HUDA HUDA SERTA MENGENALKAN KEMBALI KEPADA GENERASI MUDA DENGAN KEMASAN YANG LEBIH FRESH DAN MENARIK PERHATIAN KHUSUSNYA KAUM MILENIAL DAN ZELENIAL. TURUT AMBIL BAGIAN DALAM PENGISIAN ACARA TONGAM SIRAIT AND FRIENDS, ERIK ZENT TONDANG, ZUFENTUS PURBA, DAN MASIH BANYAK LAGI PARA PELAKU SENI YANG AKAN&nbsp; MENGISI KEGIATAN TERSEBUT. DEMIKIAN DISAMPAIKAN KEPALA DINAS PARIWISATA DAN KEBUDAYAAN SIMALUNGUN MUHAMMAD FIKRI F. DAMANIK, S.IP, M.Si DALAM SIARAN PERS NYA PAGI INI DAN BERHARAP DUKUNGAN DARI SELURUH MASYARAKAT UNTUK KESUKSESAN ACARA TERSEBUT.&nbsp;</span></p>','news/1667796183-pagelaran-budaya-dan-drama-musikal-toping-toping-dan-huda-huda-news.png','2022-11-07','Pematang Raya, Kabupaten Simalungun, Sumatera Utara, Indonesia','2022-11-07','2022-11-07');
