INSERT INTO news(id,user_id,title,slug,description,content,image,date,address,created_at,updated_at) VALUES (20,1,'TERIMA KUNJUNGAN MENTERI KEDUA PEDIDIKAN DAN  URUSAN LUAR NEGERI SINGAPURA','terima-kunjungan-menteri-kedua-pedidikan-dan-urusan-luar-negeri-singapura',NULL,'<p class="MsoNormal" style="text-align:justify"><span lang="IN">KADISBUDPAREKRAF SIMALUNGUN MEWAKILI BUPATI SIMALUNGUN MENERIMA KUNJUNGAN URUSAN
PENDIDIKAN DAN URUSAN LUAR NEGERI </span><span style="font-family: &quot;Source Sans Pro&quot;, sans-serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;">MOHAMAD MALIKI BIN OSMAN</span><span lang="IN" style="font-family: &quot;Source Sans Pro&quot;, sans-serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;"> DI PARAPAT SEHABIS MENIKMATI PEMANDANGAN DANAU TOBA
DARI&nbsp; ATAS KAPAL PHINIS PERTAMA YANG ADA
DI DANAU TOBA , KEMUDIAN PERTEMUAN DILANJUTKAN DENGAN MAKAN MALAM DI KAWASAN TOBA
CALDERA RESSORT DI KABUPATEN TOBA. <o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify"><span lang="IN">KADISBUDPAREKRAF MUHAMMAD FIKRI FANANI DAMANIK MENYAMPAIKAN BAHWA KABUPATEN
SIMALUNGUN DIANUGRAHI KEINDAHAN ALAM DARI DANAU TOBA NAMUN BUKAN HANYA ITU SAJA,
SIMALUNGUN JUGA MEMILIKI KEBUN TEH YANG TAK KALAH BAGUSNYA DENGAN YANG ADA DI
LEMBANG, DAN MASIH BANYAK POTENSI OBJEK WISATA YANG LAINNYA YANG TAK KALAH RAMAINYA
DIMINATI WISATAWAN., KEPADA MENTERI PENDIDIKAN DAN URUSAN LUAR NEGERI SINGAPURA
DISAMPAIKAN JUGA BAHWA SIMALUNGUN SIAP MENYAMBUT ADANYA INVESTASI DARI
SINGAPURA TERUTAMA DI SEKTOR PARIWISATA DAN INDUSTRI EKONOMI KREATIF. <o:p></o:p></span></p><p>



</p><p class="MsoNormal" style="text-align:justify"><span lang="IN">MOHAMAD MALIKI BIN OSMAN MENYAMPAIKAN BAHWA DIRINYA SANGAT MENIKMATI
SUASANA SAAT BERLAYAR DI PERAIRAN DANAU TOBA DENGAN MENGGUNAKAN KAPAL PHINISI, DIRINYA
KAGUM AKAN KEINDAHAN ALAM YANG TERSAJIKAN, DISAMPAIKAN JUGA BAHWA KETERTARIKAN
AGAR PRODUK TEH DARI KABUPATEN SIMALUNGUN DAPAT MENSUPLAI KONSUMSI TEH WARGA
SINGAPURA DAN ADANYA NIATAN UNTUK BERKUNJUNG KEMBALI KE SIMALUNGUN KARENA
SETELAH MENDAPAT PENJELASAN DARI KADISBUDPAREKRAF SIMALUNGUN, MENTERI PENDIDIKAN
DAN URUSAN LUAR NEGERI TERSEBUT MERASA MASIH INGIN EXPLORE LEBIH BANYAK LAGI
OBJEK WISATA DI KABUPATEN SIMALUNGUN.&nbsp;<o:p></o:p></span></p>','news/1689209850-terima-kunjungan-menteri-kedua-pedidikan-dan-urusan-luar-negeri-singapura-news.png','2023-07-11','Toba Caldera Resort, Motung, Toba, Sumatera Utara, Indonesia','2023-07-13','2023-07-13');
INSERT INTO news(id,user_id,title,slug,description,content,image,date,address,created_at,updated_at) VALUES (21,1,'Pelatihan Pemandu Ekowisata','pelatihan-pemandu-ekowisata',NULL,'<p>Dinas Kebudayaan, Pariwisata dan Ekonomi Kreatif Kabupaten Simalungun melaksanakan pelatihan kepada para pemandu objek Ekowisata yang ada di Kabupaten Simalungun, Pelatihan dilaksanakan sejak tanggal 11 sampai dengan 13 Juli 2023, Kegiatan tersebut dibuka langsung oleh Kadisbudparekraf Simalungun bapak Muhammad Fikri F Damanik,S.IP,M.Si, pada sambutan nya kadisbudparekraf menyampaikan bahwa kegiatan pelatihan seperti ini jangan hanya dianggap formalitas saja, karena setiap tahunnya trend ataupun minat wisatawan selalu berubah, oleh karenanya diharapkan melalui pelatihan ini, selain untuk menambah kompetensi para pemandu wisata, namun juga dapat menjadi sarana agar para pemandu nantinya mengetahui seperti apa minat para wisatawan di masa saat ini sehingga treatment atau pelayanan yang diberikan kepada para wisatawan dapat semakin baik kedepannya. Diakhir sambutan bapak kadis menyampaikan agar para pemandu wisata tetap menjaga nama baik pariwisata simalungun saat memberikan layanan hospitality kepada para wisatawan yang berkunjung ke simalungun, khususnya ke objek ekowisata.&nbsp; Terlihat di akhir pelatihan para peserta diajak untuk langsung praktik lapangan di beberapa objek ekowisata, diantaranya objek ekowisata harangan girsang, bangun dolok dan aek nauli.&nbsp;</p>','news/1689691870-pelatihan-pemandu-ekowisata-news.png','2023-07-13','Parapat, Kabupaten Simalungun, Sumatera Utara, Indonesia','2023-07-19','2023-07-19');
INSERT INTO news(id,user_id,title,slug,description,content,image,date,address,created_at,updated_at) VALUES (22,1,'FGD TATANG ATUR ADAT BUDAYA SIMALUNGUN TAHUN 2023','fgd-tatang-atur-adat-budaya-simalungun-tahun-2023',NULL,'<p class="MsoNormal" style="text-align: justify; "><span lang="IN">FGD TATANG ATUR
ADAT BUDAYA SIMALUNGUN <o:p></o:p></span></p><p class="MsoNormal" style="text-align: justify;"><span lang="IN">DINAS KEBUDAYAAN PARIWISATA
DAN EKONOMI KREATIF KABUPATEN SIMALUNGUN MELAKSANAKAN FOCUS GROUP DISCUSSION
DENGAN TEMA TATANG ATUR ADAT BUDAYA SIMALUNGUN YANG DILAKSANAKAN SELAMA 2 HARI PADA
TANGGAL 8 S/D 9 AGUSTUS 2023 DI HOTEL SING A SONG KECAMATAN SIANTAR. <o:p></o:p></span></p><p class="MsoNormal" style="text-align: justify;"><span lang="IN">KEGIATAN TERSEBUT
DIHADIRI LANGSUNG OLEH BUPATI SIMALUNGUN BAPAK RADIAPOH HASIHOLAN SINAGA,
SH,MH., PADA SAAT MEMBUKA KEGIATAN BUPATI MENYAMPAIKAN BAHWA KEGIATAN INI
MERUPAKAN INSTRUKSI BELIAU KEPADA KADISBUDPAREKRAF SIMALUNGUN AGAR KEDEPAN ADA
BERUPA BUKU SAKU ATAU BUKU PANDUAN YANG AKAN DIBAGIKAN KEPADA PARA TOKOH
MASYARAKAT DAN TOKOH ADAT DI SELURUH KABUPATEN SIMALUNGUN, DENGAN HARAPAN KEDEPANNYA
SETELAH ADANYA BUKU SAKU ATAU PANDUAN PELAKSANAAN ADAT TERSEBUT, ADA
KESEPAHAMAN DAN KESERAGAMAN DALAM PELAKSANAAN ADAT ISTIADAT BUDAYA SIMALUNGUN,
BAIK DALAM KEGIATAN SUKA MAUPUN DUKA, SEHINGGA BUDAYA SIMALUNGUN DAPAT TERUS
TERJAGA DAN TERLAKSANA DENGAN BAIK DALAM KEHIDUPAN BERMASYARAKAT.<o:p></o:p></span></p><p class="MsoNormal" style="text-align: justify;"><span lang="IN">KADISBUDPAREKRAF
KABUPATEN SIMALUNGUN MUHAMMAD FIKRI F DAMANIK,S.IP,M.SI YANG JUGA HADIR PADA KEGIATAN
TERSEBUT MENYAMPAIKAN BAHWA TUJUAN DILAKSANAKAN NYA FGD INI UNTUK LEBIH
MENGETAHUI LANGKAH â€“ LANGKAH JALANNYA PROSES TAHAP DEMI TAHAP DIDALAM
MENJALANKAN ADAT BUDAYA SECARA KHUSUS ADAT BUDAYA SIMALUNGUN. DAN HARAPANNYA
SETELAH MENGIKUTI FGD INI PARA PESERTA SEMAKIN MEMAHAMI TENTANG ADAT ISTIADAT
BUDAYA SIMALUNGUN, KEMUDIAN PESERTA JUGA DAPAT MENJADI PANUTAN DALAM
MENJALANKAN ADAT ISTIADAT BUDAYA SIMALUNGUN DI DAERAH MASING MASING, SERTA
PESERTA MAMPU DAN DAPAT MEMPERTAHANKAN ADAT BUDAYA SIMALUNGUN SESUAI DENGAN
YANG DIJALANKAN DALAM KEHIDUPAN BERMASYARAKAT. <o:p></o:p></span></p><p>







</p><p class="MsoNormal" style="text-align: justify; "><span lang="IN">TAMPAK SEBAGAI
NARASUMBER PADA FGD TERSEBUT, BAPAK JAN RIDWAN SUMBAYAK,SH, YAN ANWAR SARAGIH
GARINGGING,A.Md, DAN LENSIDUN SUMBAYAK, SE SEBAGAI NARASUMBER PADA KEGIATAN FGD
TERSEBUT, SEBAGAI PENUTUP KABID KEBUDAYAAN PADA DISBUDPAREKRAF SIMALUNGUN
MENYAMPAIKAN BAHWA KEGIATAN INI BERLANGSUNG SELAMA DUA HARI PADA HARI PERTAMA
DILANGSUNGKAN FGD UNTUK ADAT DAN ISTIADAT SESUAI AGAMA KRISTEN DAN DI HARI
KEDUA KHUSUS BAGI PELAKSANAAN ADAT ISTIADAT UNTUK YANG BERAGAMA ISLAM, DAN YANG
DIUNDANG MENJADI PESERTA FGD ANTARA LAIN TOKOH MASYARAKAT/ TOKOH ADAT DARI SELURUH
KECAMATAN YANG ADA DI KABUPATEN SIMALUNGUN.<o:p></o:p></span></p>','news/1691481487-fgd-tatang-atur-adat-budaya-simalungun-tahun-2023-news.png','2023-08-08','Hotel Sing A Song, Jalan Asahan, Siantar Estate, Kabupaten Simalungun, Sumatera Utara, Indonesia','2023-08-08','2023-08-08');
