INSERT INTO news(id,user_id,title,slug,description,content,image,date,address,created_at,updated_at) VALUES (24,1,'PELATIHAN PEMBUATAN KONTEN PROMOSI PARIWISATA UNTUK POKDARWIS PROGRAM HIBAH JALAN DAERAH (PHJD) TAHUN 2023','pelatihan-pembuatan-konten-promosi-pariwisata-untuk-pokdarwis-program-hibah-jalan-daerah-phjd-tahun-2023',NULL,'<p class="MsoNormal" style="text-align:justify"><span lang="IN" style="font-family:
&quot;Arial&quot;,sans-serif;mso-ansi-language:IN">PELATIHAN PEMBUATAN KONTEN PROMOSI PARIWISATA
UNTUK POKDARWIS PROGRAM HIBAH JALAN DAERAH (PHJD) TAHUN 2023<o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify"><span lang="IN" style="font-family:
&quot;Arial&quot;,sans-serif;mso-ansi-language:IN">&nbsp;</span></p><p class="MsoNormal" style="text-align:justify"><span lang="IN" style="font-family:
&quot;Arial&quot;,sans-serif;mso-ansi-language:IN">DINAS KEBUDAYAAN, PARIWISATA DAN EKONOMI
KREATIF KABUPATEN SIMALUNGUN MELAKSANAKAN PELATIHAN KEPADA ANGGOTA POKDARWIS
YANG BERADA DI PESISIR DANAU TOBA, ADAPUN&nbsp;
MATERI PELATIHAN KALI INI ADALAH PEMBUATAN KONTEN PROMOSI PARIWISATA. KEGIATAN
TERSEBUT DILAKSANAKAN SELAMA 2 HARI TANGGAL 04 DAN 05 SEPTEMBER 2023 DI HOTEL
AGAVE HARANGGAOL, KECAMATAN HARANGGAOL HORISON. <o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify"><span lang="IN" style="font-family:
&quot;Arial&quot;,sans-serif;mso-ansi-language:IN">KEPALA DINAS KEBUDAYAAN PARIWISATA DAN
EKONOMI KREATIF MUHAMMAD FIKRI FANANI DAMANIK, S.IP,M.SI MEMBUKA LANGSUNG
KEGIATAN TERSEBUT, PADA SAMBUTANNYA KADIS MENYAMPAIKAN BAHWA </span><span style="font-family:&quot;Arial&quot;,sans-serif">PROMOSI DIGITAL JUGA MEMILIKI BANYAK
SEKALI KEUNTUNGAN. PENYEBARAN INFORMASI MELALUI MEDIA DIGITAL LEBIH CEPAT
DIBANDINGKAN PROMOSI KONVENSIONAL. DALAM HITUNGAN DETIK INFORMASI MENGENAI
SUATU OBJEK WISATA BISA DISEBARLUASKAN KE SELURUH PENJURU DUNIA. DENGAN
DEMIKIAN BIAYA YANG DIBUTUHKAN DALAM MEMBUAT SUATU PROMOSI DIGITAL JUGA JAUH
LEBIH MURAH. OLEH KARENA ITU KEAHLIAN DALAM MEMBUAT SUATU KONTEN PROMOSI
PARIWISATA SECARA DIGITAL ADALAH KEBUTUHAN YANG SANGAT PENTING UNTUK DIMILIKI
OLEH SETIAP PELAKU PARIWISATA SAAT INI</span><span lang="IN" style="font-family:
&quot;Arial&quot;,sans-serif;mso-ansi-language:IN">.<o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify"><span lang="IN" style="font-family:
&quot;Arial&quot;,sans-serif;mso-ansi-language:IN">TERLIHAT PARA PESERTA YANG MERUPAKAN
ANGGOTA KELOMPOK SADAR WISATA TERLIHAT SANGAT ANTUSIAS MENDENGARKAN MATERI YANG
DISAMPAIKAN OLEH PARA NARASUMBER, MENURUT LASMARIA SITUMORANG, SE SELAKU KEPALA
BIDANG PEMASARAN PARIWISATA DI DISBUDPAREKRAF SIMALUNGUN PADA PELATIHAN INI
DIHADIRKAN 2 NARASUMBER YAKNI FERDY SIREGAR SELAKU PRAKTISI DAN ASSESOR
FOTOGRAFI TINGKAT PROVINSI DAN TEGUH SINAGA SEBAGAI INFLUENCER MUDA SIMALUNGUN
, SETELAH MENDENGARKAN PEMAPARAN TEORI PARA PESERTA DIAJAK UNTUK LANGSUNG PRAKTIK
DI OBJEK WISATA GUA BABALIANG DI DEKAT LOKASI PELATIHAN DEMIKIAN DISAMPAIKAN
LASMARIA.<o:p></o:p></span></p><p>









</p><p class="MsoNormal" style="text-align:justify"><span lang="IN" style="font-family:
&quot;Arial&quot;,sans-serif;mso-ansi-language:IN">&nbsp;</span></p>','news/1693989320-pelatihan-pembuatan-konten-promosi-pariwisata-untuk-pokdarwis-program-hibah-jalan-daerah-phjd-tahun-2023-news.png','2023-09-04','Agape Hotel Haranggaol, Jl, Haranggaol, Kabupaten Simalungun, Sumatera Utara, Indonesia','2023-09-06','2023-09-06');
INSERT INTO news(id,user_id,title,slug,description,content,image,date,address,created_at,updated_at) VALUES (25,1,'SIMALUNGUN CAR FREE DAY 2023 MERIAH','simalungun-car-free-day-2023-meriah',NULL,'<p class="MsoNormal" style="text-align:justify"><span lang="IN">SIMALUNGUN CAR FREE DAY 2023 BERLANGSUNG MERIAH</span><span style="font-size: 1rem;">&nbsp;</span></p><p class="MsoNormal" style="text-align:justify"><span lang="IN">Untuk Kali Kedua Dinas Kebudayaan, Pariwisata Dan Ekonomi Kreatif Kabupaten
Simalungun Melaksanakan Kegiatan Car Free Day Yang Dirangkai Dengan Kegiatan
Senam Massal, Gerak Jalan Massal Sambil Membersihkan Jalan Yang Dilalui, Dan
Panggung Hiburan Rakyat Bagi Masyarakat. Setelah Tahun Lalu Dilaksanakan Di
Parapat Kali Ini Lokasi Pelaksanaan Berada Di Perdagangan Kecamatan Bandar<o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify"><span lang="IN">Terlihat Hadir Pada Kegiatan Tersebut Wakil Bupati Simalungun, Hj. Zonny
Waldy, S.Sos,M.M., Sekretaris Daerah Kabupaten Simalungun Drs. Esron
Sinaga,M.Si Didampingi Sejumlah Pimpinan Opd Di Kabupaten Simalungun Dan
Beberapa Camat Yang Juga Turut Hadir Pada Kegiatan Tersebut. <o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify"><span lang="IN">Pada Sambutan Nya Bapak Zonny Waldy Menyampaikan Bahwa Kegiatan Seperti Ini
Harus Lebih Sering Dilaksanakan, Karena Selain Memupuk Kebersamaan Diantara
Masyarakat Juga Meningkatkan Kesadaran Akan Lingkungan Dimana Saat Ini
Kabupaten Simalungun Sedang Dalam Proses Menuju Adipura 2024 Dan Harapannya
Dengan Semangat Gotong Royong Yang Sama Dari Seluruh Element Masyarakat
Simalungun Dapat Meraih Adipura Di Tahun Depan. Tampak Juga Bapak Zonny Waldy
Memberikan Beberapa Pertanyaan Kepada Para Peserta Dan Memberikan Hadiah
Langsung Kepada Yang Berhasil Menjawab Termasuk Jaket Yang Saat Itu Beliau
Gunakan. <o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify"><span lang="IN">Kadisbudparekraf Simalungun, Muhammad Fikri F Damanik,S.IP,M.Si
menyampaikan bahwa kegiatan ini merupakan salah satu dari agenda rutin event
tahunan disbudparekraf simalungun, yang tahun ke tahun akan digilir lokasi
pelaksanaan nya sehingga selain memberikan hiburan bagi masyarakat juga
meningkatkan geliat ekonomi kreatif di sektor umkm.<o:p></o:p></span></p><p>











</p><p class="MsoNormal" style="text-align:justify"><span lang="IN">Dari pantauan awak media, terlihat lebih kurang 1.500 orang tampak memenuhi
lapangan balai karya murni perdagangan dan mengikuti kegiatan dengan antusias
mulai pagi hingga sore hari.&nbsp;<o:p></o:p></span></p>','news/1694995393-simalungun-car-free-day-2023-meriah-news.png','2023-09-16','Balai Karya Murni, Perdagangan I, Kabupaten Simalungun, Sumatera Utara, Indonesia','2023-09-18','2023-09-18');


